CREATE TABLE dog_robot_interactions (
    interaction_id INTEGER PRIMARY KEY AUTOINCREMENT,
    user_id INTEGER NOT NULL,
    timestamp TEXT DEFAULT (DATETIME('now')),
    command_text TEXT NOT NULL,
    response_text TEXT NOT NULL,
    emotion TEXT CHECK(emotion IN ('Senang', 'Santai', 'Bersemangat', 'Waspada', 'Setia', 'Hangat', 'Penasaran', 'Patuh')) NOT NULL,
    mode TEXT CHECK(mode IN ('Normal', 'Penjaga', 'Hiburan', 'Permainan', 'Pelatihan', 'Olahraga')) NOT NULL,
    action TEXT NOT NULL,
    response_time_ms INTEGER NOT NULL,
    context_tag TEXT CHECK(context_tag IN ('Interaksi Sosial', 'Perintah Langsung', 'Keamanan', 'Hiburan', 'Pelatihan')) NOT NULL
);

